<?php session_start(); require_once 'config/database.php'; $route=$_GET['route']??'login'; function load($c){ require 'controllers/'.$c.'.php'; return new $c;} switch($route){ case 'login': load('AuthController')->login(); break; case 'login_submit': load('AuthController')->login_submit(); break; case 'register': load('AuthController')->register(); break; case 'register_submit': load('AuthController')->register_submit(); break; case 'dashboard': load('DashboardController')->index(); break; case 'servicios': load('ServiciosController')->index(); break; case 'servicios_create': load('ServiciosController')->create(); break; case 'servicios_store': load('ServiciosController')->store(); break; case 'servicios_edit': load('ServiciosController')->edit(); break; case 'servicios_update': load('ServiciosController')->update(); break; case 'servicios_delete': load('ServiciosController')->delete(); break; case 'turnos': load('TurnosController')->index(); break; case 'turnos_create': load('TurnosController')->create(); break; case 'turnos_store': load('TurnosController')->store(); break; default: echo '404';}